function greet() {
  console.log('Hello, ' + this.name);
}
const person = { name: 'Sam' };
greet.call(person); // Hello, Sam

function makeCounter() {
  let count = 0;
  return function() {
    count++;
    return count;
  };
}
const counter = makeCounter();
console.log(counter()); // 1
console.log(counter()); // 2

